    })
    .catch((err) => {
      console.log(err);
    });

  };
  render() {
    return (
      <form onSubmit={this.SubmitForm}>
        <h3>Forgot Password</h3>
        <div className="form-group">
          <label> Email</label>
          <input
            type="email"
            className="form-control"
            placeholder="Email"
