import axios from "axios";
import React, { Component } from "react";

export default class Home extends Component {
  state={};
  componentDidMount() {
    const head = {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
    };
    axios
      .get("http://127.0.0.1:8000/api/user", head)
      .then((res) => {
        console.log(res.data);
        this.setState({
          user:res.data
        });
      })
      .catch((err) => {
        console.log(err);
      });
  }
  render() {
    if(this.state.user){
      return <h2> Welcome {this.user.first_name } {this.user.last_name}</h2>;
    }
    return <h2> you are not logged in</h2>;
  }
}
