import axios from "axios";
import React, { Component } from "react";

export default class Login extends Component {
    SubmitForm = e =>{
        e.preventDefault();
        const data = {
            email :this.email,
            password : this.password,
        };
        axios.post('http://127.0.0.1:8000/api/login',data).then(
            res => {
               localStorage.setItem('token',res.token)
            }
        ).catch(
            err => {
                console.log(err);
            }
        )
    };   
  render() {
    return (
        <form onSubmit={this.SubmitForm}>
            <h3>Login</h3>
            <div className="form-group">
                <label> Email</label>
                <input type="email" className="form-control" placeholder="Email" onChange={e=> this.email = e.target.value} />
            </div>
            <div className="form-group">
                <label> Password</label>
                <input type="password" className="form-control" placeholder="Password" onChange={e=> this.password = e.target.value} />
            </div>
            <button className="btn btn-outline-secondary btn-block">Login</button>
        </form>


    );
  }
}
